import React, { useEffect, useState  } from 'react';
import Button from '../FormInput/Button/Button';
import Label from '../FormInput/Label/Label';
import LabelWithButton from '../FormInput/LabelWithButton/LabelWithButton';
import TextInput from '../FormInput/TextInput/TextInput';
import TextInputWithButton from '../FormInput/TextInputWithButton/TextInputWithButton';
import "./PollForm.css";

import { useSelector} from 'react-redux';


//import PollProvider
import { IPoll, IPollQuestionOptions} from '../../@types/poll.d';
import Store from '../../store/Store';

interface IProps{
  children?: React.ReactNode,
} 

function PollForm(props:IProps) {

  // retreive needed data from Store
  const poll_main_question = "";
  const Poll =  useSelector<IPoll,IPoll>((state) => state);

  // Initiate states
  const [newAnswer,setNewAnswer] =useState<string>(poll_main_question);
  const [quetion,setQuetion] = useState<string>("");
  const [answerList,setAnswerList] = useState<IPollQuestionOptions[]>([{id:1,answer:"éé",votes:1}]);

  // Initiate states
  const handleAnswerChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setNewAnswer(event.target.value);
  };

  // handle question change
  const handleQuestionChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setQuetion(event.target.value);
  };

  // handle new answer 
  const handleSingleAnswerChange = (event: React.ChangeEvent<HTMLInputElement> , ind : number) => {

  };

  useEffect(()=>{
    const poll =  Store.getState().Poll;
    setQuetion(poll.poll_main_question);
    setAnswerList(poll.poll_question_options)
    console.log("Question =>",poll.poll_main_question);
  },[]);

  return(

    <section className='Section'>

      <div className='SubSection'>

        <div className='PollQuestionContainer'>

          <TextInput style={{width:"calc(100% - 99px)",marginRight:"15px"}} 
                     name="poll_question" 
                     placeholder="Type your question"
                     defaultValue={String(quetion)}
                     onChange={(event)=>{handleQuestionChange(event)}}
          />

          <div className='PollQuestion'>
              {
                answerList.map((poll,ind)=>{
                  return(
                    <TextInputWithButton key={poll.id} >
                      <TextInput style={{width:"calc(100% - 99px)",marginRight:"15px"}} 
                                 key={ind} 
                                 name="poll_question"
                                 placeholder="Type an answer"
                                 onChange={(event) => {handleSingleAnswerChange(event,ind)}}
                                 defaultValue={String(poll.answer)}
                      />
                      <Button style={{"width":"60px",height:"40px"}} text="X"/>
                    </TextInputWithButton>
                  )
                })
              }

          </div>

          <TextInputWithButton>
              <TextInput style={{width:"calc(100% - 99px)",marginRight:"15px"}} 
                         onChange={handleAnswerChange}
                         name="poll_question" 
                         placeholder="Type an answer"
              />
              <Button style={{"width":"60px",height:"40px"}} text="Add"/>
          </TextInputWithButton>

        </div>

      </div>

      <LabelWithButton>
             <Label style={{marginRight:"15px"}} content={"3/9 Possible answers"}/>
              <Button style={{minWidth:"80px",height:"40px"}} text="Reset"/>
      </LabelWithButton>

    </section>
  )
}


export default PollForm;
