import React, { useEffect, useState } from 'react';
import './App.css';
import * as io from "socket.io-client";
import Header from './components/Header/Header';
import PollForm from './components/PollForm/PollForm';
import Poll from './components/Poll/Poll';
import PollResult from './components/PollResult/PollResult';

//import PollProvider
import {PollProvider , IPoll} from '././contexts/pollContext/PollProvider';

// connect from socket with back one
//const socket = io.connect("http://localhost:3002");

function App() {

  // State 
  const [message,setMessage] = useState<string>();
  const [newMessage,setNewMessage] = useState<string>();

  // send message
  const sendMessage = () =>{
   // socket.emit("vote",{"message":message});
    setMessage("");
  }

  // Handle Socket on mount
  useEffect(()=>{
    /*socket.on("new_result",(data)=>{
      setNewMessage(data.message)
    });*/
  },[]); //socket

  //initial state
  const Store:IPoll = {
      "connected_role":"Owner", //"Owner", "User" , "Respondent"
      "poll_main_question":"What is the value of π ?",
      "poll_question_options":[
        {
          id:1,
          answer:"3.14",
          votes:12
        },
        {
          id:2,
          answer:"3.1416",
          votes:5
        },
        {
          id:3,
          answer:"3.1415",
          votes:10
        }
      ]
  }

  return (
    <PollProvider value={Store}>
        <div className="App">
        <div className='container'>
            <Header title="Sir Vote-a-lot" />
            <section className='Main'>
            <PollForm/>
            <Poll/>
            <PollResult/>
            </section>
        </div>
      </div>
    </PollProvider>
  );
}

export default App;

