import PollTypes from "./pollTypes";

interface IAction{
    type:string,
    payload:object
}

const initialState = {
    loading:true,
    polls:[],
    error:'',
    post:{}
}

const PollReducer = (state=initialState,action:IAction) =>{

    switch(action.type){

        case PollTypes.ADD_POLL :{
            return {
                ...state,
                loading:false,
                poll:action.payload,
                error:''
            }
        }

        default:{
            return state;
        }

    }

}

export default PollReducer;
