import React , {Dispatch, SetStateAction} from 'react';

// poll_question_options type
export interface IPollQuestionOptions {
    id:number,
    answer:string | number,
    votes:number
}

// export Poll interface 
export interface IPoll {
      "connected_role":"Owner" | "User" | "Respondent",
      "poll_main_question":string,
      "poll_question_options":IPollQuestionOptions[],
      "setStore"?:Dispatch<SetStateAction<IPoll>>
}

// create Provider needed dummy data just to satisfy it nothing to deal with our real data.
const PollContext = React.createContext<IPoll>({
    "connected_role":"Owner", //"Owner", "User" , "Respondent"
    "poll_main_question":"What is the value of π ?",
    "poll_question_options":[
      {
        id:1,
        answer:"3.14",
        votes:0
      },
      {
        id:2,
        answer:"3.1416",
        votes:0
      },
      {
        id:3,
        answer:"3.1415",
        votes:0
      }
    ],
    setStore: () => {}
});

export const  PollProvider = PollContext.Provider;

export default PollContext;
