import React from 'react';
import Label from '../FormInput/Label/Label';
import LabelWithButton from '../FormInput/LabelWithButton/LabelWithButton';
import { Doughnut } from 'react-chartjs-2';

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

import { Bar } from 'react-chartjs-2';

interface IProps{
  title:string
}

// Register ChartJs
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Tooltip,
  Legend
);



function PollResult({title}:IProps) {


  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top' as const,
      },
      title: {
        display: true,
        text: 'Chart.js Bar Chart',
      },
    },
  };

  const labels = ['January', 'February', 'March'];

  const data = {
    labels,
    datasets: [
      {
        label: 'Dataset 1',
        data: [100, 10, 50] ,//labels.map(() => faker.datatype.number({ min: 0, max: 1000 })),
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
      }
    ],
  };

  return (
    <section className='Section'>

      <div className='SubSection'>

        <h1>{title}</h1>

        <div className='PollQuestionContainer'>

          <div className='PollQuestion'>
             <Bar options={options} data={data} />
          </div>

          </div>

      </div>

      <LabelWithButton style={{}}>
             <Label style={{"margin-right":"15px"}} content={"Total votes : 10"}/>
      </LabelWithButton>

    </section>
  )
}

export default PollResult
