import React from 'react';
import Button from '../FormInput/Button/Button';
import LabelWithButton from '../FormInput/LabelWithButton/LabelWithButton';
import OptionList from '../FormInput/OptionList/OptionList';
import TextInput from '../FormInput/TextInput/TextInput';

interface IProps{
    title:string,
    poll_data:string[]
}

function Poll({title,poll_data}:IProps) {
  return (
    <section className='Section'>

      <div className='SubSection'>

        <h1>{title}</h1>


        <div className='PollQuestionContainer'>

          <div className='PollQuestion'>


              {
                poll_data.map((elm,ind)=>{
                  return(
                    <OptionList id={"Radio"+ind} name={elm} value={elm} type="Radio"/>
                  )
                })
              }

          </div>

          <TextInputWithButton>
              <TextInput style={{width:"calc(100% - 99px)","margin-right":"15px"}} value="" name="poll_question" placeholder="Type an answer"/>
              <Button style={{"width":"60px",height:"40px"}} text="Add"/>
          </TextInputWithButton>

          </div>

      </div>

      <LabelWithButton style={{"justify-content":"flex-end"}}>
              <Button style={{"min-width":"80px",height:"40px"}} text="Vote"/>
      </LabelWithButton>

    </section>
  )
}

export default Poll
