import React, { useEffect, useMemo, useState} from 'react';
import Header from './components/Header/Header';
import PollForm from './components/PollForm/PollForm';
import Poll from './components/Poll/Poll';
import PollResult from './components/PollResult/PollResult';

import './App.css';
import { io } from 'socket.io-client';
import { IPoll } from './@types/poll.d';
import { REFRESH_POLL_DATA_ACTION } from './store/Polls/pollActions';
import { useDispatch } from 'react-redux';

//connect client socket with back one
const socket = io.connect("http://localhost:3002");

function App() {

  // dispatch
  const dispatch = useDispatch();

  // State 
  const [message,setMessage] = useState<string>();
  const [newMessage,setNewMessage] = useState<string>();

  // handle socket
  useEffect(()=>{
    socket.on("new_result",(data:IPoll)=>{
      socket.emit("vote",{"data":data});
      dispatch(REFRESH_POLL_DATA_ACTION(data))
    });
  },[socket]);



  return (
      <div className="App">
        <div className='container'>
            <Header title="Sir Vote-a-lot" />
            <section className='Main'>
            <PollForm/>
            <Poll/>
            <PollResult/>
            </section>
        </div>
      </div>
  );

}

export default App;

