import React, { useEffect, useState } from 'react';
import Button from '../FormInput/Button/Button';
import LabelWithButton from '../FormInput/LabelWithButton/LabelWithButton';
import OptionList from '../FormInput/OptionList/OptionList';

import { IPoll } from '../../@types/poll.d';
import Store, { RootState } from '../../store/Store';

import { useSelector , useDispatch } from 'react-redux';

import { VOTING } from "../../store/Polls/pollActions";
import Empty from '../FormInput/Empty/Empty';

interface IProps{}

function Poll(props:IProps) {

  const {poll_question_options} = useSelector((state:RootState) => (state.Poll))

  const [pollQuestionOptions,setPollQuestionOptions] = useState<IPoll["poll_question_options"]>(poll_question_options);
  const [pollMainQuestion,setPollMainQuestion] = useState<string>("");

  const dispatch = useDispatch();

  const question = useSelector<IPoll,IPoll["poll_main_question"]>((state)=>(state.poll_main_question));

  useEffect(()=>{
    const poll = Store.getState().Poll;
    //setPollQuestionOptions(poll.poll_question_options);
    setPollMainQuestion(poll.poll_main_question);
  },[question]);

  const [selectedAnswer,setSelectedAnswer] = useState<string>("");

  return (
    <section className='Section'>

      {
          (pollQuestionOptions.length <= 2 )  ?
              <div className='SubSection'>
                <Empty imgSrc={"./icons/emptyPoll.png"} /> 
              </div>
          : 

          <div className='SubSection'>

            <h1>{pollMainQuestion}</h1>
            <h1>{question}</h1>

            <div className='PollQuestionContainer'>

              <div className='PollQuestion'>
                  {
                    pollQuestionOptions.map((poll,ind)=>{
                      return( <OptionList onClick={()=>{ setSelectedAnswer(String(poll.id))}} 
                        key={poll.id} id={"Radio"+ind} name="option" value={poll.answer} type="Radio"/>
                      )
                    })
                  }
              </div>

            </div>

          </div>

      }
      {
          pollQuestionOptions.length >= 3  && <LabelWithButton style={{justifyContent:"flex-end"}}>
                        <Button onClick={()=>{dispatch(VOTING(selectedAnswer))}} style={{minWidth:"80px",height:"40px"}} text="Vote"/>
                </LabelWithButton>
      }
    </section>
  )
}

export default Poll;
