import React from 'react';
import Button from '../FormInput/Button/Button';
import LabelWithButton from '../FormInput/LabelWithButton/LabelWithButton';
import OptionList from '../FormInput/OptionList/OptionList';

//import PollProvider
import PollConsumer from '../../contexts/pollContext/PollConsumer';

interface IProps{}

function Poll(props:IProps) {

  // use Pollconsumer
  const {poll_question_options, poll_main_question} =  PollConsumer();


  return (
    <section className='Section'>

      <div className='SubSection'>

        <h1>{poll_main_question}</h1>

        <div className='PollQuestionContainer'>

          <div className='PollQuestion'>

              {
                poll_question_options.map((poll,ind)=>{
                  return(
                    <OptionList key={poll.id} id={"Radio"+ind} name="option" value={poll.answer} type="Radio"/>
                  )
                })
              }

          </div>

          </div>

      </div>

      <LabelWithButton style={{justifyContent:"flex-end"}}>
              <Button style={{minWidth:"80px",height:"40px"}} text="Vote"/>
      </LabelWithButton>

    </section>
  )
}

export default Poll
