import PollTypes from "./pollTypes";

interface IAction{
    type:string,
    payload:object
}

const initialState = {
        "connected_role":"Owner", //"Owner", "User" , "Respondent"
        "poll_main_question":"What is the value of π ?",
        "poll_question_options":[
          {
            id:1,
            answer:"3.14",
            votes:12
          },
          {
            id:2,
            answer:"3.1416",
            votes:5
          },
          {
            id:3,
            answer:"3.1415",
            votes:10
          }
        ]
}

const PollReducer = (state=initialState,action:IAction) =>{

    switch(action.type){

        case PollTypes.ADD_POLL :{
            return {
                ...state,
                loading:false,
                poll:action.payload,
                error:''
            }
        }

        default:{
            return state;
        }

    }

}

export default PollReducer;
