import React, { useContext } from 'react';
import Button from '../FormInput/Button/Button';
import Label from '../FormInput/Label/Label';
import LabelWithButton from '../FormInput/LabelWithButton/LabelWithButton';
import TextInput from '../FormInput/TextInput/TextInput';
import TextInputWithButton from '../FormInput/TextInputWithButton/TextInputWithButton';
import "./PollForm.css";

//const Poll = useContext(PollContext);

interface IProps{
}

function PollForm(props:IProps) {

  const poll_data = ["3.14","3.1416","3.1415950"];

  const title = 'What is the value of π?';

  return (

    <section className='Section'>

      <div className='SubSection'>

        <div className='PollQuestionContainer'>

          <TextInput style={{width:"calc(100% - 99px)","margin-right":"15px"}} value="" name="poll_question" placeholder="Type your question"/>

          <div className='PollQuestion'>


              {
                poll_data.map((elm,ind)=>{
                  return(
                    <TextInputWithButton>
                      <TextInput style={{width:"calc(100% - 99px)","margin-right":"15px"}} key={ind} value={elm} name="poll_question" placeholder="Type an answer"/>
                      <Button style={{"width":"60px",height:"40px"}} text="X"/>
                    </TextInputWithButton>
                  )
                })
              }

          </div>

          <TextInputWithButton>
              <TextInput style={{width:"calc(100% - 99px)","margin-right":"15px"}} value="" name="poll_question" placeholder="Type an answer"/>
              <Button style={{"width":"60px",height:"40px"}} text="Add"/>
          </TextInputWithButton>

        </div>

      </div>

      <LabelWithButton>
             <Label style={{"margin-right":"15px"}} content={"3/9 Possible answers"}/>
              <Button style={{"min-width":"80px",height:"40px"}} text="Reset"/>
      </LabelWithButton>

    </section>
  )
}

export default PollForm
