import React, { useEffect } from 'react';
import Label from '../FormInput/Label/Label';
import LabelWithButton from '../FormInput/LabelWithButton/LabelWithButton';
import { Bar } from 'react-chartjs-2';
import ChartDataLabels from 'chartjs-plugin-datalabels';

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Tooltip
} from 'chart.js';


//import PollProvider
import { IPollQuestionOptions} from '../../contexts/pollContext/PollProvider';
import PollConsumer from '../../contexts/pollContext/PollConsumer';


interface IProps{}

// Register ChartJs
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Tooltip,
  ChartDataLabels
);

function PollResult(props:IProps) {

  // use Pollconsumer
  const {poll_question_options,poll_main_question,total_votes} =  PollConsumer();

  // generate label array
  const dynamicLabels = () =>{
    return(poll_question_options.map((poll,ind)=>{
        return poll.answer;
    }));
  }

  useEffect(()=>{

      // ChartJs display options
      const options = {
        responsive: true,
        plugins: {
          legend: {
            position: 'top' as const,
          },
          title: {
            display: true,
            text: 'Chart.js Bar Chart',
          },
        },
        scales: {
            y: {
              grace: '5%'
            }
        }
      };

      // ChartJs display labels
      const labels = dynamicLabels();

      // ChartJs data
      const data = {
        labels,
        datasets: [
          {
            label: poll_main_question,
            data: [2, 5, 10 ] ,//labels.map(() => faker.datatype.number({ min: 0, max: 1000 })),
            backgroundColor: ["red", "blue", "green"],
            datalabels: {
              align: 'end' as const,
              anchor: 'end' as const,
              formatter: function(value:string, ctx:object) {
                  return value;
              },
              color:'black',
              font:{
                  size: 14,
                  weight:500
              }
            }
          }
        ]
      };

  })

  return (
    <section className='Section'>

      <div className='SubSection SubSectionCenter'>

        <h1>{poll_main_question}</h1>

        <div className='PollQuestionContainer'>

          <div className='PollQuestion'>
             <Bar options={options} data={data} />
          </div>

          </div>

      </div>

      <LabelWithButton style={{}}>
             <Label style={{"margin-right":"15px"}} content={`Total votes : ${total_votes}`}/>
      </LabelWithButton>

    </section>
  )
}

export default PollResult
