import React, { useContext } from 'react';
import PollContext from '../../PollContext';
import Button from '../FormInput/Button/__test__/Button';
import TextInput from '../FormInput/TextInput/TextInput';
import TextInputWithButton from '../FormInput/TextInputWithButton/TextInputWithButton';
import "./PollForm.css";

//const Poll = useContext(PollContext);

interface IProps{
  children?: React.ReactNode,
  poll_data:string[]
}

function PollForm({poll_data}:IProps) {
  return (

    <section className='Section'>

      <div className='PollQuestionContainer'>

        <TextInput style={{width:"96%"}} value="" name="poll_question" placeholder="Type your question"/>

        <div className='PollQuestion'>


            {
              poll_data.map((elm,ind)=>{
                return(
                  <TextInputWithButton>
                    <TextInput style={{width:"calc(100% - 150px)"}} key={ind} value={elm} name="poll_question" placeholder="Type an answer"/>
                    <Button style={{"width":"60px",height:"40px"}}text="X"/>
                  </TextInputWithButton>
                )
              })
            }

        </div>

      </div>

    </section>
  )
}

export default PollForm
