import React from 'react';
import "./OptionList.css";

interface IProps{
    id:string,
    name:string,
    value:string | number,
    type:string,
    onChange:()=>void
}

function OptionList({
    id,
    name,
    value,
    type
  }:IProps) {
  return (
    <div className="OptionListContainer">
        <input className="OptionListInput" type={type} id={id} name={name} value={value} onChange={onChange}/>
        <label className="OptionListLabel" htmlFor={id}>{value}</label>
    </div>
  )
}

export default OptionList;
