import React, { useEffect, useState } from 'react';
import './App.css';
import * as io from "socket.io-client";
import Header from './components/Header/Header';
import PollForm from './components/PollForm/PollForm';
import Poll from './components/Poll/Poll';
import PollResult from './components/PollResult/PollResult';

//import PollProvider
import { PollProvider } from './PollContext';

// connect from socket with back one
const socket = io.connect("http://localhost:3002");



function App() {

  // State 
  const [message,setMessage] = useState<string>();
  const [newMessage,setNewMessage] = useState<string>();

  // send message
  const sendMessage = () =>{
    socket.emit("vote",{"message":message});
    setMessage("");
  }

  // Handle Socket on mount
  useEffect(()=>{
    socket.on("new_result",(data)=>{
      setNewMessage(data.message)
    });
  },[socket]);

  /*
  {
    connected-role:"Owner", "User" , "Respondent",
    poll-main-question:"",
    poll-question-options:[
      {
        id:1,
        question:"3.14",
        votes:0
      },
      {
        id:2,
        question:"3.1416",
        votes:0
      },
      {
        id:3,
        question:"3.1415",
        votes:0
      }
    ],
    total_votes:0
  }
  */

  return (
    <PollProvider value={{}}>
        <div className="App">
        <div className='container'>
            <Header title="Sir Vote-a-lot" />
            <section className='Main'>
            <PollForm poll_data={["3.14","3.1416","3.1415950"]}></PollForm>
            <Poll     poll_data={["3.14","3.1416","3.1415950"]} title='What is the value of π?'/>
            <PollResult title='What is the value of π'/>
            </section>
        </div>
      </div>
    </PollProvider>
  );
}

export default App;

