    title:string
}

function Poll({title}:IProps) {
  return (
    <section className='Section'>
      <div className='SubSection'>
        <h1>{title}</h1>
      </div>

      <LabelWithButton>
             <TextInput style={{width:"calc(100% - 99px)","margin-right":"15px"}} value="" name="poll_question" placeholder="Type an answer"/>
              <Button style={{"width":"60px",height:"40px"}} text="red"/>
      </LabelWithButton>

    </section>
  )
}

export default Poll
