import React, { useContext } from 'react';
import PollContext from '../../PollContext';
import TextInput from '../FormInput/TextInput/TextInput';
import "./PollForm.css";

//const Poll = useContext(PollContext);

interface IProps{
  children?: React.ReactNode,
  poll_data:string[]
}

function PollForm({poll_data}:IProps) {
  return (

    <section className='Section'>

      <div className='PollQuestionContainer'>

        <TextInput value="" name="poll_question" placeholder="Type your question"/>

        <div className='PollQuestion'>
            {
              poll_data.map((elm,ind)=>{
                return(
                  <TextInput key={ind} value={elm} name="poll_question" placeholder="Type an answer"/>
                )
              })
            }
        </div>

      </div>

    </section>
  )
}

export default PollForm
