import React ,{ useState , useEffect } from 'react';
import Label from '../FormInput/Label/Label';
import LabelWithButton from '../FormInput/LabelWithButton/LabelWithButton';
import { Bar } from 'react-chartjs-2';
import ChartDataLabels from 'chartjs-plugin-datalabels';

import { useSelector} from 'react-redux';

import { IPoll } from '../../@types/poll.d';
import Store, { RootState } from '../../store/Store';

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Tooltip
} from 'chart.js';
import Empty from '../FormInput/Empty/Empty';

interface IProps{}

// Register ChartJs
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Tooltip,
  ChartDataLabels
);

const PollResult = (props:IProps) => {


  const {poll_question_options, poll_main_question} = useSelector((state:RootState) => (state.Poll));

  const [pollQuestionOptions,setPollQuestionOptions] = useState<IPoll["poll_question_options"]>(poll_question_options);
  const [pollMainQuestion,setPollMainQuestion] = useState<IPoll["poll_main_question"]>(poll_main_question);

  const question = useSelector<IPoll,IPoll["poll_main_question"]>((state)=>(state.poll_main_question));

  useEffect(()=>{
  },[]);

  // generate label array
  const dynamicLabels = () =>{
    return(pollQuestionOptions.map((poll,ind)=>{
        return poll.answer;
    }));
  }

  // generate vote rates array
  const dynamicVotes = () =>{
    return(pollQuestionOptions.map((poll,ind)=>{
        return poll.votes;
    }));
  }

  // generate vote rates array
  const totalVotes = () =>{
    return(pollQuestionOptions.reduce((acc,poll)=>{
        return acc + poll.votes
    },0));
  }

  // ChartJs display options
  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top' as const,
      },
      title: {
        display: true,
        text: 'Chart.js Bar Chart',
      },
    },
    scales: {
        y: {
          grace: '5%'
        }
    }
  };

  // ChartJs display labels
  const labels = dynamicLabels();

  // ChartJs data
  const data = {
    labels,
    datasets: [
      {
        data: dynamicVotes(),
        backgroundColor: ["red", "blue", "green"],
        datalabels: {
          align: 'end' as const,
          anchor: 'end' as const,
          formatter: function(value:string, ctx:object) {
              return value;
          },
          color:'black',
          font:{
              size: 14,
              weight:500
          }
        }
      }
    ]
  };

  return (
    <section className='Section'>
      { 
          pollQuestionOptions.length < 3  ?
          <div className='SubSection SubSectionCenter'>
                <Empty imgSrc={"./icons/emptyChart.png"} /> 
              </div>
            : 

          <div className='SubSection SubSectionCenter'>

            <h1>{pollMainQuestion}</h1>

            <div className='PollQuestionContainer'>

              <div className='PollQuestion'>
                <Bar options={options} data={data} />
              </div>

            </div>

          </div>
      }
      {
          pollQuestionOptions.length >= 3  &&  <LabelWithButton style={{}}>
                <Label style={{marginRight:"15px"}} content={`Total votes : ${totalVotes()}`}/>
          </LabelWithButton>
      }
    </section>
  )
}

export default PollResult
