import PollTypes from "./pollTypes";

interface IAction{
    type:string,
    payload:object | string | number
}

const initialState = {
    "connected_role":"Owner", //"Owner", "User" , "Respondent"
    "poll_main_question":"What is the value of π ?",
    "poll_question_options":[
        {
        id:1,
        answer:"3.14",
        votes:12
        },
        {
        id:2,
        answer:"3.1416",
        votes:5
        },
        {
        id:3,
        answer:"3.1415",
        votes:10
        },

    ]
}

const PollReducer = (state=initialState,action:IAction) =>{

    switch(action.type){

        case PollTypes.ADD_POLL :{
            return Object.assign({...state, poll_question_options:[...state.poll_question_options,{
                id:5,
                answer:action.payload,
                votes:0
            }]});
        }

        case PollTypes.UPDATE_QUESTION :{
            return  Object.assign({...state, poll_main_question:action.payload});
        }

        case PollTypes.REMOVE_AN_ANSWER :{

            const prev_state = Object.assign({...state});

            const new_poll_question_options = prev_state.poll_question_options.filter((poll)=>{
                return poll.id !== action.payload
            });

            return Object.assign({...state, poll_question_options:new_poll_question_options});
        }

        default:{
            return state;
        }

    }

}

export default PollReducer;
